import Pages from './pages';
// import {
//   BrowserRouter as Router,
//   Route,
// } from 'react-router-dom';

function App() {
  // useState Hook to change the state of the user
  const[user, setuser]= useState(null);

  useEffect(()=>{
    fetch('https://gitconnected.com/v1/portfolio/shrinivas221')
      .then(res=> res.json())
      .then(user=>{
        setuser(user);
      });
  },[]);

  if (!user){
    return <div>
      <h1>USER NOT FOUND</h1>
    </div>;
  }
  return <Pages user={user}/>;
}

